export default {
  name: 'ContactForm',
  props: {
    type: {
      type: String,
      default: 'particulier'
    }
  },
  data() {
    return {
      valid: false,
      loading: false,
      formSubmitted: false,
      form: {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        company: '',
        service: '',
        message: '',
        terms: false
      },
      nameRules: [v => !!v || 'Ce champ est requis', v => v.length >= 2 || 'Minimum 2 caractères'],
      emailRules: [v => !!v || 'E-mail est requis', v => /.+@.+\..+/.test(v) || 'E-mail doit être valide'],
      phoneRules: [v => !!v || 'Téléphone est requis', v => /^[0-9\s\-+()]{10,15}$/.test(v) || 'Téléphone doit être valide'],
      requiredRules: [v => !!v || 'Ce champ est requis'],
      serviceTypes: ['Diagnostic énergétique', 'Optimisation énergétique', 'Certification énergétique', 'Conseil en rénovation', 'Autre']
    };
  },
  computed: {
    isProClient() {
      return this.type === 'professionnel';
    }
  },
  methods: {
    async submitForm() {
      if (this.$refs.form.validate()) {
        this.loading = true;

        // Simulation d'un appel API
        await new Promise(resolve => setTimeout(resolve, 1500));
        this.formSubmitted = true;
        this.loading = false;

        // Réinitialiser le formulaire après 3 secondes
        setTimeout(() => {
          this.$refs.form.reset();
          this.formSubmitted = false;
        }, 3000);
      }
    }
  }
};
